            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>

      <Tabs defaultValue="milk" className="space-y-4" onValueChange={(value) => setActiveTab(value as "milk" | "meat")}>
        <TabsList>
          <TabsTrigger value="milk">
            <Droplet className="mr-2 h-4 w-4" />
