        <TabsContent value="milk" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Producción de Leche</CardTitle>
              <CardDescription>Últimos 30 días</CardDescription>
            </CardHeader>
            <CardContent className="h-80">
              <ResponsiveContainer width="100%" height="100%">
                <LineChart
                  data={milkChartData}
